import React, { Component } from 'react';
import API from './utils/API';





    class EmployeeCard extends Component {
        state = {
          firstName: "",
          lastName: "",
          username: "",
          email: "",
          cell: "",
          location: "",
          picture: "",
          gender: ""

        };


        componentDidMount() {
          this.searchName("https://randomuser.me/api/?results=200&nat=us");
        }

        searchName = query => {
          API.search(query)
            .then( (res) => {
               this.setState({firstName: res.data.results[0].name.first})
               this.setState({lastName: res.data.results[0].name.last})
               this.setState({email: res.data.results[0].email})
               this.setState({cell: res.data.results[0].cell})
               this.setState({picture: res.data.results[0].picture.medium})
               this.setState({gender: res.data.results[0].gender.toUpperCase()})

               console.log((res.data.results[0].gender))
            })

            .catch(err => console.log(err));
        };

        handleInputChange = event => {
          const name = event.target.name;
          const value = event.target.value;
          this.setState({
            [name]: value
          });
        };

        // When the form is submitted, search the Giphy API for `this.state.search`
        handleFormSubmit = event => {
          event.preventDefault();
          this.searchName(this.state.search);
        };

        render() {
          return (
            <div>
              <Table striped bordered hover>
  <thead>
    <tr>
      <th>Image</th>      
      <th>First Name</th>
      <th>Last Name</th>
      <th>Email</th>

    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <td>3</td>
      <td colSpan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</Table>
          <h1>{ this.state.firstName }</h1>
          <h1>{ this.state.lastName }</h1>
          <h1>{ this.state.email }</h1>
          <h1>{ this.state.cell }</h1>
          <h3>{ this.state.gender }</h3>
          <img src={ this.state.picture} alt=""/>


            </div>
          );
        }
      }


    // API.search()
